import { Component, Input } from '@angular/core';
import { faCircleCheck } from '@fortawesome/free-solid-svg-icons';
import { faCircleExclamation } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-toast-box-modal',
  templateUrl: './toast-box-modal.component.html',
  styleUrls: ['./toast-box-modal.component.scss']
})
export class ToastBoxModalComponent {

  faCircleCheck=faCircleCheck;
  faCircleExclamation=faCircleExclamation;

  @Input()icon: string = "success";
  @Input()message: string = 'abc';
  constructor() { }
  

  ngOnInit(): void {
  }
  
}
<div class="toast-message shadow">
    <div class="toast-message__icon">
        <fa-icon [icon]="faCircleCheck" class="toast-message__icon--success" *ngIf="icon=='success'"></fa-icon>
        <fa-icon [icon]="faCircleExclamation" class="toast-message__icon--error" *ngIf="icon=='error'"></fa-icon>
        
    </div>
    <div class="toast-message__content">
        <!-- [innerHTML]="message" -->
        {{ message }}
        <!-- <span (click)="autoLogIn()" *ngIf="autoSignIn">Tự động đăng nhập</span> -->
    </div>
</div>
